import React, { Component } from 'react'
import ChefService from '../services/ChefService';

class CreateChefComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            // step 2
            id: this.props.match.params.id,
            firstname: '',
            lastname: '',
            gender:'',
            emailid: '',
            paaword:'',
            address: '',
            city: '',
            contactnumber: ''

        }
        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
        this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
        this.saveOrUpdateChef = this.saveOrUpdateChef.bind(this);
    }

    // step 3
    componentDidMount(){

        // step 4
        if(this.state.id === '_add'){
            return
        }else{
            ChefService.getChefById(this.state.chefid).then( (res) =>{
                let chef = res.data;
                this.setState({firstname: chef.firstname,
                    lastname: chef.lastname,
                    emailid : chef.emailid,
                   // address : chef.address.ChefService,
                    gender :chef.gender,
                    address : chef.address,
                    city    : chef.city,
                    contactnumber:chef.contactnumber


                });
            });
        }        
    }
    saveOrUpdateChef = (c) => {
        c.preventDefault();
        let chef = {firstname: this.state.firstname, lastname: this.state.lastname, emailid: this.state.emailid,gender: this.state.gender,password: this.state.paasword,
            address: this.state.address,city: this.state.city,contactnumber: this.state.contactnumber,};
        console.log('chef => ' + JSON.stringify(chef));

        // step 5
        if(this.state.id === '_add'){
            ChefService.createChef(chef).then(res =>{
                this.props.history.push('/chefs');
            });
        }else{
            ChefService.updateChef(chef, this.state.chefid).then( res => {
                this.props.history.push('/chefs');
            });
        }
    }
    
    changeFirstNameHandler= (event) => {
        this.setState({firstname: event.target.value});
    }

    changeLastNameHandler= (event) => {
        this.setState({lastname: event.target.value});
    }

    changeEmailHandler= (event) => {
        this.setState({emailid: event.target.value});
    }

    changeGenderHandler= (event) => {
        this.setState({gender: event.target.value});
    }

    changeAddressHandler= (event) => {
        this.setState({address: event.target.value});
    }

    changeCityHandler= (event) => {
        this.setState({city: event.target.value});
    }

    changePasswordHandler= (event) => {
        this.setState({password: event.target.value});
    }

    changeContactNumberHandler= (event) => {
        this.setState({contactnumber: event.target.value});
    }

    cancel(){
        this.props.history.push('/chefs');
    }

    getTitle(){
        if(this.state.id === '_add'){
            return <h3 className="text-center">Add Chef</h3>
        }else{
            return <h3 className="text-center">Update Chef</h3>
        }
    }
    render() {
        return (
            <div>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                {
                                    this.getTitle()
                                }
                                <div className = "card-body">
                                    <form>
                                        <div className = "form-group">
                                            <label> First Name: </label>
                                            <input placeholder="First Name" name="firstname" className="form-control" 
                                                value={this.state.firstname} onChange={this.changefirstnameHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Last Name: </label>
                                            <input placeholder="Last Name" name="lastname" className="form-control" 
                                                value={this.state.lastname} onChange={this.changelastnameHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Gender: </label>
                                            <input type="radio" name="gender" id="female" className="form-control" 
                                                value={this.state.gender} onChange={this.changegendernameHandler}/>
                                        </div>

                                        <div className = "form-group">
                                            <label> Gender: 
                                            <input 
                                                type="radio" 
                                                name="gender"
                                                id="male" 
                                                className="form-control" 
                                                checked={this.state.selectedOption === "Male"}
                                                onChange={this.onValueChange}
                                            />
                                            </label>
                                        </div>

                                        
                                        <div className = "form-group">
                                            <label> Email Id: </label>
                                            <input placeholder="Email Address" name="emailid" className="form-control" 
                                                value={this.state.emailid} onChange={this.changeemailidHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Password: </label>
                                            <input placeholder="Password" name="password" className="form-control" 
                                                value={this.state.password} onChange={this.changepassworddHandler}/>
                                        </div>

                                        <div className = "form-group">
                                            <label> Address: </label>
                                            <input placeholder="Address" name="address" className="form-control" 
                                                value={this.state.address} onChange={this.changeaddressHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> City </label>
                                            <input placeholder="City" name="city" className="form-control" 
                                                value={this.state.city} onChange={this.changecityHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Contact Number </label>
                                            <input placeholder="Contact" name="contactnumber" className="form-control" 
                                                value={this.state.contactnumber} onChange={this.changecontactnumberHandler}/>
                                        </div>


                                        <button className="btn btn-success" onClick={this.saveOrUpdateEmployee}>Save</button>
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default CreateChefComponent
